mapper.select:
SELECT
o.*
FROM t_order o

	where o.teacher_id=#{teacher_id}


	limit #{top},#{pageSize}

SELECT o.* FROM t_order o
LEFT JOIN t_student s ON s.`c_orderid`=o.`id`
WHERE s.`id`=#{student_id}
limit #{top},#{pageSize}

mapper.insert:
INSERT INTO t_order ( one, two, three, flag, startDate, endDate, teacher_id, count
)VALUES( #{one}, #{two}, #{three}, #{flag}, #{startDate}, #{endDate}, #{teacher_id}, #{count}
)
